INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following is contributed towards the extra stability oflyophilic colloids ','(a)Hydration','(b)Charge','(c)Colour','(d)Tyndall effect','','Surface Chemistry','2','Answer = (a)Lyophillic means liquid loving hence hydration is contributed toward theextra stability of lyophillic colloids.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following methods is used for sol destruction','(a)Condensation','(b)Dialysis','(c)Diffusion through animal membrane','(d)Addition of an electrolyte','4','Surface Chemistry','2','Answer =  (d)Traces of electrolytes are essential for stabilising the sales hence forsales destruction addition of electrolytes are required.',CURRENT_TIMESTAMP);
