INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which one of the following is an incorrect statement for physisorption ','(a)It is a reversible process','(b)It requires less heat of adsorption','(c)It requires activation energy','(d)It takes place at low temperature','3','Surface Chemistry','2','Answer =  (c)Physiorption is a process in which the particles of adsorbate are heldto the surface of adsorbent by physical forces hence does not requires activationenergy.',CURRENT_TIMESTAMP);
