INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A catalyst is used in a reaction to','(a)Change the nature of reaction products','(b)Increase the reaction yield','(c)Decrease the need for reactants','(d)Decrease the time required for the reaction','','Surface Chemistry','2','Answer = (d)A catalyst is used to decrease the time required for the reaction henceit can decease or increase the rate of reaction.',CURRENT_TIMESTAMP);
