INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The capacity to bring about coagulation increases with','(a)Ionic radii','(b)Atomic radii','(c)Valency of an ion','(d)Size of an ion','3','Surface Chemistry','2','Answer =  (c)The amount of electrolyte required to coagulate a fixed amount of a soldepends upon the valency of flocculating ion.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Gold number gives','(a)The amount of gold present in the colloid','(b)The amount of gold required to break thecolloid','(c)The amount of gold required to protect thecolloid','(d)None of these','4','Surface Chemistry','2','Answer =  (d)Gold no. is a measure of protective power of a lyophillic colloid.',CURRENT_TIMESTAMP);
