INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The function of gum-arabic in the preparation of  indian ink is','(a)Coagulation ','(b)Peptization','(c)Protective action','(d)Absorption','3','Surface Chemistry','2','Answer =  (c)Gum-arabic has protective power hence the function of it ion inpreparation of indian ink is protective action.',CURRENT_TIMESTAMP);
