INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Urea','(a)Is an amide of carbonic acid','(b)It is diamide of carbonic acid','(c)Gives carbonic acid on hydrolysis','(d)Resembles carbonic acid','','Chemical Kinetics-Final','2','Answer = (b)Urea is the diamide of carbonic acid.\\(\\mathop {HO - \\mathop {\\mathop C\\limits^{||} }\\limits^O  -OH}\\limits_{{\\rm{carbonic acid}}}  + 2N{H_3}\\mathop {{H_2}N - \\mathop {\\mathopC\\limits^{||} }\\limits^O  - N{H_2}}\\limits_{{\\rm{Urea}}}\\)So two mole of \\(N{H_3}\\) required that why it is the diamide of carbonic acid.',CURRENT_TIMESTAMP);
