INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following does not represent the correct order of the propertyindicated','(a)\\(S{c^{3 + }} > C{r^{3 + }} > F{e^{3 + }} > M{n^{3 + }}\\) ionic radii','(b)\\(Sc < Ti < Cr < Mn\\) Density','(c)\\(M{n^{2 + }} > N{i^{2 + }} < C{o^{2 + }} < F{e^{2 + }}\\) ionic radii','(d)\\(FeO < CaO > MnO > CuO\\) Basic nature','1','Chemical Periodicity','2','Answer =  (a)\\(S{c^{3 + }} > C{r^{3 + }} > F{e^{3 + }} > M{n^{3 + }}\\) the correctorder is \\(C{r^{ + 3}} > M{n^{ + 3}} > F{e^{ + 3}} > S{c^{ + 3}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The order of magnitude of ionic radii of ions \\(N{a^ + },M{g^{2 + }},A{l^{3+ }}\\) and \\(S{i^{4 + }}\\) is ','(a)\\(N{a^ + } < M{g^{2 + }} < A{l^{3 + }} < S{i^{4 + }}\\)','(b)\\(M{g^{2 + }} > N{a^ + } > A{l^{3 + }} > S{i^{4 + }}\\)','(c)\\(A{l^{3 + }} > N{a^ + } > S{i^{4 + }} > M{g^{2 + }}\\)','(d)\\(N{a^ + } > M{g^{2 + }} > A{l^{3 + }} > S{i^{4 + }}\\)','4','Chemical Periodicity','2','Answer =  (d)\\(N{a^ + } > M{g^{2 + }} > A{l^{3 + }} > S{i^{4 + }}\\). All areisoelectronic but nuclear charge per electron is greatest for \\(S{i^{4 + }}\\). Soit has smallest size and nuclear charge per electron for \\(N{a^ + }\\) issmallest. So it has largest size.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The order of the magnitude of ionic radii of ions \\({N^{3 - }},{O^{2 - }}\\)and \\({F^ - }\\)is ','(a)\\({N^{3 - }} > {O^{2 - }} > {F^ - }\\)','(b)\\({N^{3 - }} < {O^{2 - }} < {F^ - }\\)','(c)\\({N^{3 - }} > {O^{2 - }} > {F^ - }\\)','(d)\\({N^{3 - }} < {O^{2 - }} > {F^ - }\\)','1','Chemical Periodicity','2','Answer =  (a)\\({N^{3 - }} > {O^{2 - }} > {F^ - }\\). All are isoelectronic but nuclearcharge per electron is highest for \\({F^ - }\\), so it has smallest size.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following ion is the smallest ion','(a)\\(O_2^ + \\)','(b)\\(O_2^ - \\)','(c)\\({O_2}\\)','(d)\\(O_2^{ - 2}\\)','1','Chemical Periodicity','2','Answer =  (a) Cation has small size than parent atom and anion has greater size thanparent atom.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The correct order of radii is','(a)\\(N < Be < B\\)','(b)\\({F^ - } < {O^{2 - }} < {N^{3 - }}\\)','(c)\\(Na < Li < K\\)','(d)\\(F{e^{3 + }} < F{e^{2 + }} < F{e^{4 + }}\\)','2','Chemical Periodicity','2','Answer =  (b)Ionic radii decreases significantly from left to right in a period amongrepresentative elements.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which one of the following should be most stable','(a)\\(H_2^ + \\)','(b)\\({H^ + }\\)','(c)\\(H\\)','(d)\\({H^ - }\\)','4','Chemical Periodicity','2','Answer =  (d)\\({H^ - }\\) is most stable due to its full filled 1s-orbital.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following ionic radius would be maximum','(a)\\({C^{4 - }}\\)','(b)\\({N^{3 - }}\\)','(c)\\({O^{2 - }}\\)','(d)\\(M{g^{2 + }}\\)','1','Chemical Periodicity','2','Answer =  (a)\\({C^{4 - }}\\) has largest radius due to least nuclear charge perelectron.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which is helpful in the formation of ionic bond','(a)Only small cation','(b)Only small anion','(c)Small cation and small anion both','(d)Low positive charge, large cation and small anion','4','Chemical Periodicity','2','Answer =  (d)For ionic bond formation low I.E., high electron affinity and highlattice energy is needed.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following has largest ionic radius','(a)\\(C{s^ + }\\)','(b)\\(L{i^ + }\\)','(c)\\(N{a^ + }\\)','(d)\\({K^ + }\\)','1','Chemical Periodicity','2','Answer =  (a)Ionic radii increases in a group.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Point out the wrong statement :On moving horizontally from left to right across a period in the periodic table','(a)Metallic character decreases','(b)Electronegativity increases','(c)Gram atomic volume first decreases and then increases','(d)Size of the atoms increases for normal elements','4','Chemical Periodicity','2','Answer =  (d)Size of elements decreases across a period.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following statements is correct','(a)\\({X^ - }\\) ion is larger in size than X atom','(b)\\({X^ + }\\) ion is larger in size than X atom','(c)\\({X^ + }\\) ion is larger in size than \\({X^ - }\\) ion','(d)\\({X^ + }\\) and \\({X^ - }\\) ions are equal in size','1','Chemical Periodicity','2','Answer =  (a)\\({X^ - }\\) ion larger in size than \\(X\\) atoms. Because of loweffective nuclear charge on \\({X^ - },\\,X\\) has a bigger size.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The atomic radius of elements of which of the following series would benearly the same','(a)\\(Na\\)\\(K\\)\\(Rb\\)\\(Cs\\)','(b)\\(Li\\)\\(Be\\)\\(B\\)\\(C\\)','(c)\\(Fe\\)\\(Co\\)\\(Ni\\)\\(Cu\\)','(d)\\(F\\)\\(Cl\\)\\(Br\\)\\(I\\)','3','Chemical Periodicity','2','Answer =  (c)\\(Fe,\\,Co,\\,Ni,\\,Cu\\). Due to shielding of d-electrons, the effect ofincreased nuclear charge due to increase in atomic no. neutralised. Consequentlyatomic radius remains almost unchanged after chromium.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The decreasing order of size of isoelectronic series \\({K^ + },\\,C{a^{2 +}},\\,C{l^ - }\\) and \\({S^{2 - }}\\) is','(a)\\({K^ + } > C{a^{2 + }} > {S^{2 - }} > C{l^ - }\\)','(b)\\({K^ + } > C{a^{2 + }} > C{l^ - } > {S^{2 - }}\\)','(c)\\(C{a^{2 + }} > {K^ + } > C{l^ - } > {S^{2 - }}\\)','(d)\\({S^{2 - }} > C{l^ - } > {K^ + } > C{a^{2 + }}\\)','4','Chemical Periodicity','2','Answer =  (d)\\({S^{2 - }} > C{l^ - } > {K^ + } > C{a^{2 + }}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In which of the following pairs the difference between the covalent radii ofthe two metals is maximum','(a)K, Ca','(b)Mn , Fe','(c)Co , Ni','(d)Cr , Mn','1','Chemical Periodicity','2','Answer =  (a)Covalent radii decreases on going from left to right in periods. Howeveramong the transition elements the size do not changes much because the electronsadd to the pneultimate d-subshells i.e. \\((n - 1)\\,d{\\rm{ - }}\\)subshell.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','When a neutral atom is converted into cation, there is','(a)Decrease in the atomic number','(b)An increase in the atomic number','(c)A decrease in size','(d)An increase in size','3','Chemical Periodicity','2','Answer =  (c)During the conversion of neutral atom to cation size decreases becauseafter removal one \\({e^ - }\\) or more(i) Nuclear charge per electron increases.(ii) Outermost shell is completely removed.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A trend common to both groups I and VII elements in the periodic table asatomic number increases is','(a)Oxidising power increases','(b)Atomic radius increases','(c)Maximum valency increases','(d)Reactivity with water increases','','Chemical Periodicity','2','Answer = (b)Atomic radius increases as no. of shells increases.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Chloride ion and potassium ion are isoelectronic. Then','(a)Potassium ion is relatively bigger','(b)Depends on the other cation and anion','(c)Their size are same','(d)Chloride ion is bigger than potassium ion','4','Chemical Periodicity','2','Answer =  (d)Chloride ion and potassium ion are isoelectronic, isoelectronic ions arethose ions having same number of electrons.\\(K = 2,\\,8,\\,8,\\,1\\)\\({K^ + } = 2,\\,8,\\,8\\)\\(Cl = 2,\\,8,\\,7\\)\\(Cl = 2,\\,8,\\,8\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following has the largest ionic radius','(a)\\(N{a^ + }\\)','(b)\\(N{i^ + }\\)','(c)\\(C{s^ + }\\)','(d)\\(M{g^{ + 2}}\\)','3','Chemical Periodicity','2','Answer =  (c)\\(C{s^ + }\\)has the largest ionic radius in the periodic table.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The ionic radii of \\(L{i^ + },N{a^ + },{K^ + }\\)are in which of thefollowing order ','(a)\\({K^ + } > N{a^ + } > L{i^ + }\\)','(b)\\({K^ + } > N{a^ + } < L{i^ + }\\)','(c)\\({K^ + } < N{a^ + } < L{i^ + }\\)','(d)\\(L{i^ + } > N{a^ + } < {K^ + }\\)','1','Chemical Periodicity','2','Answer =  (a)Ionic radii increases down the group.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following has smallest size','(a)\\(M{g^{2 + }}\\)','(b)\\(N{a^ + }\\)','(c)\\(A{l^{3 + }}\\)','(d)\\(S{i^{4 + }}\\)','4','Chemical Periodicity','2','Answer =  (d)\\(S{i^{4 + }}\\)is smallest in size due to their greater +ve charge.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which one of the following is expected to have largest size','(a)\\({F^ - }\\)','(b)\\({O^{ - 2}}\\)','(c)\\(A{l^{ + 3}}\\)','(d)\\({N^{ - 3}}\\)','4','Chemical Periodicity','2','Answer =  (d)Due to having three electrons atomic size increases.\\({F^ - } = 9 + 1 = 10\\) electrons ; \\({O^{ - 2}} = 8 + 2 = 10\\) electrons\\(Al + 3 = 13 - 3 = 10\\) electrons ; \\({N^{3 - }} = 7 + 3 = 10\\,{e^ - }.\\)Because electrostatic force between nucleus and \\(\\bar e\\) cloud is least innitrogen.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The trivalent ion having largest size in lanthanide series is','(a)\\(Ti\\)','(b)Zr','(c)Hf','(d)La','4','Chemical Periodicity','2','Answer =  (d)The trivalent ion having largest size in lanthanide series is lanthanum.This is due to lanthanide contraction.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following alkali metal ions has lowest ionic mobility inaqueous solutions  ','(a)\\(R{b^ + }\\)','(b)\\(C{s^ + }\\)','(c)\\(L{i^ + }\\)','(d)\\(N{a^ + }\\)','3','Chemical Periodicity','2','Answer =  (c)As we know that hydration power decreases on moving down the group henceamong alkali metals Li has excessive hydration & hence it has low mobility inaqeous solution.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Ionic radii are   [CBSE PMT , 04]','(a)Directly proportional to effective nuclear charge','(b)Directly proportional to square of effective nuclear charge','(c)Inversely proportional to effective nuclear charge','(d)Inversely proportional to square of effective nuclear charge.','3','Chemical Periodicity','2','Answer =  (c)Ionic radius in the n\\(^{th}\\) orbit is given by \\({r_n} =\\frac{{{n^2}a}}{z}\\) or \\({r_n} \\propto \\frac{1}{Z}\\) where n is principalequation no., ao. bohr\\'s radius of hydrogen atom and Z is the effective nuclearenergy.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The correct sequence of increasing covalent character is represented by  ','(a)\\(LiCl < NaCl < BeC{l_2}\\)','(b)\\(BeC{l_2} < NaCl < LiCl\\)','(c)\\(NaCl < LiCl < BeCl\\)','(d)\\(BeC{l_2} < LiCl < NaCl\\)','','Chemical Periodicity','2','Answer = (c)Order of polarising power \\(B{e^{ +  + }} > L{i^ + } > N{a^ + }\\)Hence order of covalent character \\(BeC{l_2} > LiCl > NaCl\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Correct energy value order is   ','(a)\\(ns\\;np\\;nd(n - 1)f\\)','(b)\\(ns\\;np(n - 1)d\\;(n - 2)f\\)','(c)\\(ns\\;np(n - 1)d\\;(n - 1)f\\)','(d)\\(ns(n - 1)d\\;n(n - 1)f\\)','2','Chemical Periodicity','2','Answer =  (b)Higher the (n+1) value higher is the energy associated with orbitals.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The ionic conductance of following cation in a given concentration are inthe order   ','(a)\\(L{i^ + } < N{a^ + } > {K^ + } < R{b^ + }\\)','(b)\\(L{i^ + } > N{a^ + } > {K^ + } > R{b^ + }\\)','(c)\\(L{i^ + } < N{a^ + } > {K^ + } > R{b^ + }\\)','(d)\\(L{i^ + } = N{a^ + } < {K^ + } < R{b^ + }\\)','1','Chemical Periodicity','2','Answer =  (a)With the increase in size of cation the size of the hydrated iondecreases hence ionic conductance increases.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The incorrect statement among the following is','(a)The first ionisation potential of \\(Al\\) is less than the first ionisationpotential of \\(Mg\\)','(b)The second ionisation potential of \\(Mg\\) is greater than the secondionisation potential of \\(Na\\)','(c)The first ionisation potential of \\(Na\\) is less than the first ionisationpotential of \\(Mg\\)','(d)The third ionisation potential of \\(Mg\\) is greater than the third ionisationpotential of \\(Al\\)','','Chemical Periodicity','2','Answer = (b)I.E.(II) of \\(Na\\) is higher than that of \\(Mg\\) because in case of\\(Na\\), the second \\({e^ - }\\) has to be remove from the noble gas core while incase of \\(Mg\\) removal of second \\({e^ - }\\) gives a noble gas core.\\(Mg\\) has high first ionisation potential than \\(Na\\) because of its stable\\(n{s^2}\\) configuration.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In halogens, with the increase of atomic number which habit is found','(a)Habit to loose electrons decreases','(b)Ionic radii decreases','(c)Ionization potential decreases','(d)In \\(M{X_2}\\,(M = \\) metal and \\(X = \\) halogen), covalent propertiesdecreases','3','Chemical Periodicity','2','Answer =  (c)Ionization potential decreases. Since, atomic size increases.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Ionization potential is lowest for','(a)Halogens','(b)Inert gases','(c)Alkaline earth metals','(d)Alkali metals','4','Chemical Periodicity','2','Answer =  (d)Alkali metals, lower the no. of valence \\({e^ - }\\), lower is the valueof ionization potential.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following explanation is best for not placing hydrogen in eitherthe group of alkali metals or halogens','(a)The ionization energy of hydrogen is to high for group of alkali metals, buttoo low of halogen group','(b)Hydrogen can form compounds with all other elements','(c)Hydrogen is much lighter element than the alkali metals or the halogens','(d)None of the above','1','Chemical Periodicity','2','Answer =  (a)The ionization energy of hydrogen is to high for group of alkali metals,but too low for halogen group.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Highest energy will be absorbed to eject out the electron in theconfiguration','(a)\\(1{s^2}2{s^2}2{p^1}\\)','(b)\\(1{s^2}2{s^2}2{p^3}\\)','(c) \\(1{s^2}2{s^2}2{p^2}\\)','(d)\\(1{s^2}2{s^2}2{p^4}\\)','2','Chemical Periodicity','2','Answer =  (b)Due to high stability of half-filled orbitals.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In which of the following process highest energy is absorbed','(a)\\(Cu \\to C{u^ + }\\)','(b)\\(Br \\to B{r^ - }\\)','(c)\\(I \\to {I^ - }\\) ','(d)\\(Li \\to L{i^ + }\\)','1','Chemical Periodicity','2','Answer =  (a)In \\(Cu\\) it has completely filled d-orbital so highest energy isabsorbed when it convert in \\(C{u^ + }\\)ion.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','How many ionisation energies can carbon have','(a)1','(b)2','(c)4','(d)6','3','Chemical Periodicity','2','Answer =  (c) The energy required to remove an electron from outermost orbit of anisolated gaseous atom is called I.E. Now carbon has \\(4{e^ - }\\) in outermostshell. Thus it has 4 ionization energies.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following gaseous atoms has highest value of \\(IE\\)','(a)\\(P\\)','(b)\\(Si\\)','(c)\\(Mg\\)','(d)\\(Al\\)','1','Chemical Periodicity','2','Answer =  (a)Since, stable half filled configuration.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The first ionization potentials \\(\\left( {eV} \\right)\\) of \\(Be\\) and \\(B\\)respectively are','(a)\\(8.29eV\\), \\(9.32eV\\)','(b)\\(9.32eV\\), \\(9.32eV\\)','(c)\\(8.29eV\\), \\(8.29eV\\)','(d)\\(9.32eV\\), \\(8.29eV\\)','','Chemical Periodicity','2','Answer = (d)First I.P. of \\(Be > B\\) because of stable \\(n{s^2}\\) configuration.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which ionisation potential \\(\\left( {IP} \\right)\\) in the following equationsinvolves the greatest amount of energy','(a)\\(Na \\to N{a^ + } + {e^ - }\\)','(b)\\({K^ + } \\to {K^{2 + }} + {e^ - }\\)','(c)\\({C^{2 + }} \\to {C^{3 + }} + {e^ - }\\)','(d)\\(C{a^ + } \\to C{a^{2 + }} + {e^ -}\\)','2','Chemical Periodicity','2','Answer =  (b)\\({K^ + } \\to {K^{2 + }} + {e^ - }\\). Since \\({e^ - }\\) is to be removedfrom stable configuration.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The first four ionization energy values of an element are   and kcal. The number of valence electrons in the element is','(a)1','(b)2','(c)3','(d)4','3','Chemical Periodicity','2','Answer =  (c)Since the IV, I.E. is very high. Thus electron is to be removed fromstable configuration.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following has least ionization potential[CPMT , 93]','(a)Li','(b)Cs','(c)Cl','(d)I','2','Chemical Periodicity','2','Answer =  (b)\\(Li\\) and \\({A_2}{B_3}\\) belong to I\\(^{st}\\) group but \\(Cs\\) haslarger size, hence low nuclear attraction force, thus low ionization energy.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following element has the lowest ionization potential ','(a)Fe','(b)H','(c)Li ','(d)He','3','Chemical Periodicity','2','Answer =  (c)\\(Li\\) belongs to I\\(^{st}\\) group. There is 1\\({e^ - }\\) in outermostshell. Thus low I.E.',CURRENT_TIMESTAMP);
