INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The formation of the oxide ion \\(O_{(g)}^{2 - }\\) requires first anexothermic and then an endothermic step as shown below\\({O_{(g)}} + {e^ - } = O_{(g)}^ - \\Delta {H^0} =  - \;kJmo{l^{ - 1}}\\)\\(O_{(g)}^ -  + {e^ - } = O_{(g)}^{2 - }\\Delta {H^0} = \;kJmo{l^{ - 1}}\\)This is because','(a)\\({O^ - }\\) ion will tend to resist the addition of another electron','(b)Oxygen has high electron affinity','(c)Oxygen is more electronegative','(d)\\({O^ - }\\)ion has comparatively larger size than oxygen atom','1','Chemical Periodicity','2','Answer =  (a)The addition of second electron in an atom or ion is always endothermic.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which is correct about ionisation potential ','(a)It is independent of atomic radii','(b)It increases with increase in atomic radii','(c)It remains constant with increase in atomic radii','(d)It decreases with increase in atomic radii','','Chemical Periodicity','2','Answer = (d)We know that ionisation potential gradually decreases of moving down thegroup while atomic size increases as we move down the group. Hence larger theatomic size, smaller is ionisation potential.',CURRENT_TIMESTAMP);
