INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The cooling in refrigerator is due to  ','(a)Reaction of the refrigerator gas','(b)Expansion of ice','(c)The expansion of the gas in therefrigerator','(d)The work of the compressor','3','Theromodynamics and Thermochemistry-Final','2','Answer =  (c)This is based on Joule-Thomson effect.',CURRENT_TIMESTAMP);
