INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following is not a state function','(a)Internal energy','(b)Enthalpy','(c)Work','(d)Entropy','3','Theromodynamics and Thermochemistry-Final','2','Answer =  (c)Work is not a state function as during a process its value depends onthe path followed. The value of enthalpy, internal energy and entropy depends onthe state and not on the path followed to get that state, hence these are statefunctions.',CURRENT_TIMESTAMP);
