INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Consider the reaction : \\({N_2} + 3{H_2} \\to 2N{H_3}\\) carried out atconstant temperature and pressure. If \\(\\Delta H\\) and \\(\\Delta U\\) are theenthalpy and internal energy changes for the reaction, which of the followingexpression is true ','(a)\\(\\Delta H = 0\\)','(b)\\(\\Delta H = \\Delta U\\)','(c)\\(\\Delta H < \\Delta U\\)','(d)\\(\\Delta H > \\Delta U\\)','3','Theromodynamics and Thermochemistry-Final','2','Answer =  (c)At constant P or T\\(\\Delta H = \\Delta U + \\Delta nRT\\)\\(\\Rightarrow{}\\)  \\(\\Delta n = {n_p} -{n_R} = 2 - 4 =  - 2\\)\\(\\therefore \\)\\(\\Delta H < \\Delta U.\\)',CURRENT_TIMESTAMP);
