INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The work done in ergs for the reversible expansion of one mole of an idealgas from a volume of 10 litres to 20 litres at \\({25^o}C\\) is','(a)\\(2.303 \\times 298 \\times 0.082\\,\\log \\,2\\)','(b)\\(298 \\times {10^7} \\times 8.31 \\times 2.303\\,\\log \\,2\\)','(c)\\(2.303 \\times 298 \\times 0.082\\,\\log \\,0.5\\)','(d)\\(8.31 \\times {10^7} \\times 298 - 2.303\\,\\log \\,0.5\\)(e)\\(2.303 \\times 298 \\times 2\\,\\log \\,2\\)','2','Theromodynamics and Thermochemistry-Final','2','Answer =  (b)\\(W = 2.Answer = \,\\,nRT\\log \\frac{{{V_2}}}{{{V_1}}}\\)\\(= 2.Answer = \,\\, \\times 1\\,\\, \\times 8.Answer = \,\\, \\times {10^7}\\, \\times Answer = \,\\log\\frac{{20}}{{10}}\\)\\(= Answer = \,\\, \\times {10^7}\\, \\times 8.Answer = \,\\, \\times 2.Answer = \,\\,\\log \\,\\,2.\\)',CURRENT_TIMESTAMP);
