INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','When \\(P\\) reacts with caustic soda, the products are \\(P{H_3}\\) and\\(Na{H_2}P{O_2}.\\) This reaction is an example of','(a)Oxidation','(b)Reduction','(c)Oxidation and reduction (Redox)','(d)Neutralization','3','Redox reactions','2','Answer =  (c)\\(\\mathop {{P_4}}\\limits^0  + 3NaOH + 3{H_2}O \\to \\mathop{3Na{H_2}P{O_2}}\\limits_{\\,\\,\\,\\,\\,\\,\\,{\\rm{Sodium}}{\\rm{hypophosphite}}}  + \\mathop {P{H_3}}\\limits^{ -3\\,\\,\\,\\,\\,\\,\\,\\,\\,} \\).It shows oxidation and reduction (Redox)  properties.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the reaction \\({H_2}S + N{O_2} \\to {H_2}O + NO + S.\\) \\({H_2}S\\) is','(a)Oxidised','(b)Reduced','(c)Precipitated','(d)None of these','1','Redox reactions','2','Answer =  (a)In this reaction \\({H_2}S\\) is oxidised because the oxidation state of\\'S\\' change from - 2 to 0.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The conversion of \\(Pb{O_2}\\) to \\(Pb{(N{O_3})_2}\\)is','(a)Oxidation','(b)Reduction','(c)Neither oxidation nor reduction','(d)Both oxidation and reduction','2','Redox reactions','2','Answer =  (b)\\(\\mathop {Pb{O_2}}\\limits^{ + 4\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,}  \\to \\mathop{Pb{{(N{O_3})}_2}}\\limits^{ + 2\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,} \\).In this reaction reduction occurs.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the course of a chemical reaction an oxidant','(a)In neutral solution','(b)In acidic solution','(c)In non-polar solvent','(d)In alkalinesolution','2','Redox reactions','2','Answer =  (b)Any substance which is capable of oxidising other substances and iscapable of accepting/gaining electron during oxidation is called oxidising agentor oxidant.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the reaction \\(3Mg + {N_2} \\to M{g_3}{N_2}\\)','(a)Magnesium is reduced','(b)Magnesium isoxidized','(c)Nitrogen is oxidized','(d)None of these','2','Redox reactions','2','Answer =  (b)In the given reaction oxidation state of Mg is changing from 0 to +2while in nitrogen it is changing from 0 to -3. So oxidation of Mg and reductionof nitrogen takes place.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','When sodium metal is dissolved in liquid ammonia, blue colour solution isformed. The blue colour is due to','(a)Solvated \\(N{a^ + }\\)ions','(b)Solvated electrons','(c)Solvated \\(NH_2^ - \\) ions','(d)Solvated protons','2','Redox reactions','2','Answer =  (b)When sodium metal is dissolved in liquid ammonia to form colouredsolution. Dilute solutions are bright blue in colour due to the presence ofsolvated electrons.\\(Na + (x + y)N{H_3} \\to {[Na{(N{H_3})_x}]^ + } + \\mathop {{{[e{{(N{H_3})}_y}]}^ -}}\\limits_{{\\rm{Blue Colour}}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Oxidation involves ','(a)Loss of electrons','(b)Gain of electrons','(c)Increase in the valency of negative part','(d)Decrease in the valency of positive part','1','Redox reactions','2','Answer =  (a)It is the process in which electrons are lost (de-electronation).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Incorrect statement regarding rusting is ','(a)Metallic iron is oxidised to \\(F{e^{3 + }}\\)ions','(b)Metallic iron is reduced to \\(F{e^{2 - }}\\) ions','(c)Oxygen gas is reduced to oxide ion','(d)Yellowish - brown product is formed','2','Redox reactions','2','Answer =  (b)\\(4Fe + 3{O_2} \\to 4F{e^{3 + }} + 6{O^{2 - }}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','When copper turnings are added to silver nitrate solution, a blue colouredsolution is formed after some time. It is because, copper','(a)Displaces silver from the solution','(b)Forms a blue coloured complex with\\(AgN{O_3}\\)','(c)Is oxidised to \\(C{u^{2 + }}\\)','(d)Is reduced to \\(C{u^{2 + }}\\)','3','Redox reactions','2','Answer =  (c)Cu is above of Ag in electrochemical series and thus \\(Cu + 2A{g^ + }\\to C{u^{2 + }} + 2Ag\\) reaction occurs.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','When \\(S{n^{2 + }}\\)changes to \\(S{n^{4 + }}\\)in a reaction','(a)It loses two electrons','(b)It gains twoelectrons','(c)It loses two protons','(d)It gains twoprotons','','Redox reactions','2','Answer = (a)\\(S{n^{2 + }} \\to S{n^{4 + }} + 2{e^ - }\\). In this reaction \\(S{n^{2 +}}\\) change in \\(S{n^{4 + }}\\) it is called an oxidation reaction.',CURRENT_TIMESTAMP);
