INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','One gas bleaches the colour of flowers by reduction while the other byoxidation','(a)\\(CO\\) and \\(C{l_2}\\)','(b)\\(S{O_2}\\) and \\(C{l_2}\\)','(c)\\({H_2}S\\) and \\(B{r_2}\\)','(d)\\(N{H_3}\\) and \\(S{O_2}\\)','2','Redox reactions','2','Answer =  (b)\\(S{O_2}\\) bleaches by reduction while chlorine bleaches colour offlowers by oxidation.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In a reaction between zinc and iodine, in which zinc iodide is formed, what isbeing oxidised','(a)Zinc ions','(b)Iodide ions','(c)Zinc atom','(d)Iodine','','Redox reactions','2','Answer = (c)\\(\\mathop {Zn}\\limits^o  + \\mathop {{I_2}}\\limits^o  \\to \\mathop{Zn{I_2}}\\limits^{ + 2\\,\\, - 1\\,\\,\\,} \\)In this reaction \\(Zn\\)atom oxidised to \\(\\mathop {Z{n^{2 + }}}\\limits^{} \\) ionand iodine reduced to \\({I^ - }\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which one of the  following reactions does not involve either oxidation orreduction ','(a)\\(VO_2^ + \\, \\to \\,{V_2}{O_3}\\)','(b)\\(Na\\, \\to \\,N{a^ + }\\)','(c)\\(CrO_4^{2 - }\\, \\to \\,C{r_2}O_7^{2 - }\\)','(d)\\(Z{n^{2 + }}\\, \\to \\,Zn\\)','3','Redox reactions','2','Answer =  (c)\\(\\mathop {CrO_4^{2 - }}\\limits^{ * \\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,}\\)\\(\\mathop {C{r_2}O_7^{2 - }}\\limits^{ * \\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,} \\)\\(x + [( - 2) \\times 4] =  - 2\\)\\(2x + ( - 2) \\times 7 =  - 2\\)\\(x = 8 - 2 =  + 6\\)\\(2x = 14 - 2 = 12,\\)\\(x = \\frac{{12}}{2} =  + 6\\)In this reaction oxidation and reduction are not involved because there is nochange in oxidation number.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the following reaction,\\(3B{r_2} + 6CO_3^{2 - } + 3{H_2}O = 5B{r^ - } + BrO_3^ -  + 6HC{O_3}\\)[MP PMT , 95]','(a)Bromine is oxidised and carbonate isreduced','(b)Bromine is reduced and water is oxidised','(c)Bromine is neither reduced nor oxidised','(d)Bromine is both reduced and oxidised','4','Redox reactions','2','Answer =  (d)\\(\\mathop {3B{r_2}}\\limits^0  + 6CO_3^{2 - } + 3{H_2}O \\to \\mathop{5B{r^ - }}\\limits^{ - 1}  + \\mathop {BrO_3^ - }\\limits^{ + 5\\,\\,\\,\\,\\,\\,\\,\\,}  +6HC{O_3}\\). In this reaction bromine is oxidised as well as reduced.',CURRENT_TIMESTAMP);
