INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The compound \\(YB{a_2}C{u_3}{O_7}\\) which shows superconductivity has copper inoxidation state ........ Assume that the rare earth element Yttrium is in itsusual \\( + \\,3\\) oxidation state','(a)3/7','(b)7/3','(c)3','(d)7','2','Redox reactions','2','Answer =  (b)\\(B{a_2}\\mathop {C{u_3}}\\limits^{ * \\,\\,} {O_7}\\)\\(3 + 2 \\times 2 + 3x - (2 \\times 7) = 0\\)\\(3 + 4 + 3x - 14 = 0\\)\\(3x = 7\\)\\(x = \\frac{7}{3}.\\)',CURRENT_TIMESTAMP);
