INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The conductivity of a saturated solution of \\(BaS{O_4}\\) is \\(3.06 \\times {10^{- 6}}\\,oh{m^{ - 1}}\\,c{m^{ - 1}}\\) and its equivalent conductance is\\(1.53\\,oh{m^{ - 1}}\\,c{m^{ - 1}}\\,equivalen{t^{ - 1}}\\). The \\({K_{sp}}\\) of the\\(BaS{O_4}\\) will be','(a)\\(4\\, \\times {10^{ - 12}}\\) ','(b)\\(2.5 \\times {10^{ - 9}}\\)','(c)\\(2.5 \\times {10^{ - 13}}\\)','(d)\\(4 \\times {10^{ - 6}}\\)','4','Redox reactions','2','Answer =  (d)\\(\\lambda m = \\frac{{Answer = \\,K}}{S} = \\frac{{Answer =  \\times 3.06 \\times{{10}^{ - 6}}}}{S} = 1.53\\)\\(S = 2 \\times {10^{ - 3}}\\frac{{mol}}{{litre}}\\)\\({K_{sp(BaS{O_4})}} = {S^2} = {(2 \\times {10^{ - 3}})^2} = 4 \\times {10^{ - 6}}.\\)',CURRENT_TIMESTAMP);
