INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which one of the following complexes is an outer orbital complex','(a)\\({[Co{(N{H_3})_6}]^{3 + }}\\)','(b)\\({[Mn{(CN)_6}]^{4 - }}\\)','(c)\\({[Fe{(CN)_6}]^{4 - }}\\)','(d)\\({[Ni{(N{H_3})_6}]^{2 + }}\\)Atomic nos : \\(Mn = 25,\\,Fe = 26,\\,Co = 27,\\,Ni = 28\\)','4','Coodination chemistry','2','Answer =  (d)Complex ionHybridization of central atom\\({[Fe{(CN)_6}]^{4 - }}\\)\\({d^2}s{p^3}\\) (inner)\\({[Mn{(CN)_6}]^{4 - }}\\)\\({d^2}s{p^3}\\) (inner)\\({[Co{(N{H_3})_6}]^{3 + }}\\)\\({d^2}s{p^3}\\) (inner)\\({[Ni{(N{H_3})_6}]^{2 + }}\\)\\(s{p^3}{d^2}\\) (outer)',CURRENT_TIMESTAMP);
