INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','On complete combustion 1.4 g hydrocarbon gave 1.8 g water. Empirical formulaof the hydrocarbon is','(a)\\(CH\\)','(b)\\(C{H_2}\\)','(c)\\(C{H_3}\\)','(d)\\(C{H_4}\\)','2','Purification and organic compounds','2','Answer =  (b) 1.8gm water obtained from 1.4gm hydrocarbon\\(\\therefore \\)18gm water obtained from - \\(\\frac{{1.4}}{{1.8}} \\times 18 =14\\) gm.Empirical formula Mass = 14\\(\\therefore \\) Empirical formula = \\(C{H_2}\\).',CURRENT_TIMESTAMP);
