INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Empirical formula of a compound is \\({C_2}{H_5}O\\) and its molecular weightis 90. Molecular formula of the compound is','(a)\\({C_2}{H_5}O\\)(b)\\({C_3}{H_6}{O_3}\\)(c)\\({C_4}{H_{10}}{O_2}\\)(d)\\({C_5}{H_{14}}O\\)518.60 g of a compound on analysis gave \\(C = 24{\\rm{ }}g,\\) \\(H = 4{\\rm{ }}g\\)and O = 32 g. Its Empirical formula is[CPMT 1971, 81]','','(b)\\({C_3}{H_6}{O_3}\\)(c)\\({C_4}{H_{10}}{O_2}\\)(d)\\({C_5}{H_{14}}O\\)518.60 g of a compound on analysis gave \\(C = 24{\\rm{ }}g,\\) \\(H = 4{\\rm{ }}g\\)and O = 32 g. Its Empirical formula is[CPMT 1971, 81](a)\\({C_2}{H_4}{O_2}\\)','(b)\\({C_2}{H_2}O\\)(c)\\(C{H_2}{O_2}\\)(d)\\(C{H_2}O\\)','3','Purification and organic compounds','2','Answer =  (c)Empirical formula mass =\\({C_2}{H_5}O\\) = 24+ 5 +16= 45.\\(n = \\frac{{{\\rm{Mol}}{\\rm{. mass}}}}{{{\\rm{Emp}}{\\rm{. mass}}}} =\\frac{{90}}{{45}} = 2\\)Mol. formula = \\({({C_2}{H_5}O)_2} = {C_4}{H_{10}}{O_2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound contains \\(C = 38.8\\% ,\\) \\(H = 16\\% \\) and \\(N = 45.2\\%\\). Empirical formula of the compound is[CPMT , 83]','(a)\\(C{H_3}N{H_2}\\)','(b)\\(C{H_3}CN\\)','(c)\\({C_2}{H_5}CN\\)','(d)\\(C{H_2}{(NH)_2}\\)','1','Purification and organic compounds','2','Answer =  (a)Element                 No. of Moles        Simple Ratio\\(\\begin{array}{p{47pt}p{61pt}p{61pt}}{C = 38.8} & 38.8/12 = 3.2 & 1 \\\\{H = 16} & 16/1 = 16 & 5 \\\\{N = 45.2} & 45.2/14 = 3.2 & 1 \\\\\\end{array}\\)Therefore, Empirical formula = \\(C{H_5}N\\) or \\(C{H_3}N{H_2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In Kjeldahl\\'s method for the estimation of nitrogen, the formula used is','(a)\\(\\% N = \\frac{{1.4{\\rm{ }}V{\\rm{ }}W}}{N}\\)','(b)\\(\\% N = \\frac{{1.4{\\rm{}}N{\\rm{ }}W}}{V}\\)','(c)\\(\\% N = \\frac{{V{\\rm{ }}N{\\rm{ }}W}}{{1.8}}\\)','(d)\\(\\% N = \\frac{{1.4{\\rm{}}V{\\rm{ }}N}}{W}\\)','4','Purification and organic compounds','2','Answer =  (d)% of \\(N = \\frac{{1.4 \\times V \\times N}}{W}\\)where V = Volume of acid usedN = Normality of acid, W = Weight of substance',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound on analysis gave the following results : C = 54.5%, O =36.4%, H = 9.1%. The Empirical formula of the compound is','(a)\\(C{H_3}O\\)','(b)\\({C_2}{H_4}O\\)','(c)\\({C_3}{H_4}O\\)','(d)\\({C_4}{H_8}O\\)','','Purification and organic compounds','2','Answer = (b)Element                 No. of Moles         Simple Ratio\\(\\begin{array}{p{47pt}p{61pt}p{61pt}}{C = 54.5} & 54.5/12 = 4.54 & 2 \\\\{H = 9.1} & 9.1/1 = 9.1 & 4 \\\\{O = 36.4} & 36.4/16 = 2.27 & 1 \\\\\\end{array}\\)Hence, \\({C_2}{H_4}O\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound gave C = 92.31% and \\(H = 7.69\\% \\). If molecularweight of the compound is 78, its molecular formula is','(a)\\({C_6}{H_6}\\)','(b)\\({C_7}{H_7}\\)','(c)\\({C_6}{H_{18}}\\)','(d)\\({C_8}{H_{20}}\\)','1','Purification and organic compounds','2','Answer =  (a)Element                No. of Moles         Simple Ratio\\(\\begin{array}{p{40pt}p{68pt}p{61pt}}{C = 92.31} & 92.31/12 = 7.69 & 1 \\\\{H = 7.69} & 7.69/1 = 7.69 & 1 \\\\\\end{array}\\)Hence, CHEmpirical formula mass of CH = 13\\(n = \\frac{{{\\rm{Mol}}{\\rm{. mass}}}}{{{\\rm{Emp}}{\\rm{.}}\\,{\\rm{mass}}}} =\\frac{{78}}{{13}} = 6\\)Molecular formula = \\({(CH)_6} = {C_6}{H_6}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound gave the following results \\(C = 53.3\\% ,{\\rm{ }}H =15.6,{\\rm{ }}N = 31.1\\% ,\\) mol. wt. = 45,What is molecular formula of the compound ?','(a)\\({C_2}{H_5}{N_2}\\)','(b)\\({C_2}{H_5}N\\)','(c)\\({C_2}{H_7}N\\)','(d)\\({C_2}{H_6}N\\)','3','Purification and organic compounds','2','Answer =  (c) Element                  No. of Moles         Simple Ratio\\(\\begin{array}{p{47pt}p{61pt}p{61pt}}{C = 53.3} & 53.3/12 = 4.44 & 2 \\\\{H = 15.6} & 15.6/1 = 15.6 & 7 \\\\{N = 31.1} & 31.1/14 = 2.22 & 1 \\\\\\end{array}\\)Hence, formula = \\({C_2}{H_7}N\\) \\((C{H_3}C{H_2}N{H_2})\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A compound gave 80% carbon and 20 % hydrogen on analysis. The compound ispossibly','(a)\\({C_6}{H_6}\\)','(b)\\({C_2}{H_5}OH\\)','(c)\\({C_2}{H_6}\\)','(d)\\(CHC{l_3}\\)','3','Purification and organic compounds','2','Answer =  (c)Element                   No. of Moles        Simple Ratio\\(\\begin{array}{p{47pt}p{61pt}p{61pt}}{C = 80} & 80/12 = 6.66 & 1 \\\\{H = 20} & 20/1 = 20 & 3 \\\\\\end{array}\\)Hence formula = \\(C{H_3}\\) or \\({C_2}{H_6}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A compound has 50% carbon, 50% oxygen and approximate molecular weight is Its molecular formula is','(a)CO','(b)\\({C_4}{O_3}\\)','(c)\\({C_{12}}{O_9}\\)','(d)\\({C_3}{O_3}\\)','3','Purification and organic compounds','2','Answer =  (c)Elements        Simple ratio\\(C\\,\\,\\,\\,\\, = \\,\\,\\,\\,\\,\\,\\,50\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,50/12\\,\\,\\,\\, = \\,\\,\\,\\,4\\)\\((C{H_2}O)\\)Empirical formula = \\({C_4}{O_3}\\)Empirical formula mass = 96\\(n\\,\\, = \\,\\,\\frac{{Answer = }{{96}} = 3\\)Molecular formula = \\({({C_4}{O_3})_3} = {C_{12}}{O_9}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','On analysis, a saturated hydrocarbon is found to contain 83.70 percentcarbon and 16.30% hydrogen. The empirical formula will be (at. wt. of C=12, at.wt. of H = 1)','(a)\\({C_3}{H_6}\\)','(b)\\({C_3}{H_8}\\)','(c)\\({C_3}{H_7}\\)','(d)\\({C_6}{H_{12}}\\)','3','Purification and organic compounds','2','Answer =  (c)  Element             No. of moles         Simple ratio\\(\\begin{array}{p{44pt}p{58pt}p{84pt}}{C = 83.7\\%H = 16.3\\%} & 83.7/12 = 6.9 & 6.9/6.9 = 1 \\times{} 3 = 3 \\\\ & 16.3/1 = 16.3 & 16.3/0.9 = 2.3 \\times{} 3 = 7 \\\\\\end{array}\\)Empirical formula = \\({C_3}{H_7}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound has C = 60%, H = 13.3% and O = 26.7%. Its empiricalformula will be','(a)\\({C_3}{H_6}O\\)','(b)\\({C_2}{H_6}{O_2}\\)','(c)\\({C_4}{H_8}{O_2}\\)','(d)\\({C_3}{H_8}O\\)','4','Purification and organic compounds','2','Answer =  (d)Elements                   No. of moles        Simple ratio\\(\\begin{array}{p{13pt}p{26pt}p{67pt}p{53pt}}C & 60\\% & 60/12 = 5 & 3.01 \\\\H & 13.3\\% & 13.3/1 = 13.3 & 8.01 \\\\O & 26.7\\% & 26.7/16 = 1.66 & 1 \\\\\\end{array}\\)Empirical formula = \\({C_3}{H_8}O\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A hydrocarbon has C=85.72% and remaining H. The hydrocarbon is','(a)\\(C{H_2}O\\)','(b)\\({C_2}{H_4}O\\)','(c)\\(C{H_4}O\\)','(d)\\({C_2}{H_8}{O_2}\\)','1','Purification and organic compounds','2','Answer =  (a)Element                  No. of moles         Simple ratio\\(\\begin{array}{p{13pt}p{31pt}p{58pt}p{58pt}}C & 85.72\\% & 85.72/12 & 7.14 = 1 \\\\H & 14.18\\% & 14.18/1 & 14.18 = 2 \\\\\\end{array}\\)Empirical formula = \\({C_2}{H_4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound contains C, H and O in the proportion of 6 : 1 : 8 byweight, respectively. Its vapour density is 30. Its molecular formula will be','(a)\\({C_2}{H_4}{O_2}\\)','(b)\\(C{H_4}O\\)','(c)\\(C{H_2}O\\)','(d)\\({C_3}HO\\)','1','Purification and organic compounds','2','Answer =  (a)Elements            No. of moles               Simple ratio\\(\\begin{array}{p{13pt}p{22pt}p{76pt}p{49pt}}C & 6 & 6/12 = 0.5 = 1 & 1 \\\\H & 1 & 1/1 = 1 = 2 & 2 \\\\O & 8 & 8/16 = 0.5 = 1 & 1 \\\\\\end{array}\\)Thus, Empirical formula = \\(\\mathop {{C_6}{H_5} - \\mathop {\\mathop C\\limits_{||}}\\limits_O  - CH = \\mathop {\\mathop {C\\,\\,\\,}\\limits_{|\\,\\,\\,\\,} }\\limits_{OH}  -C{H_3}}\\limits_{{\\rm{(enol form)}}} \\)Empirical formula mass = 30Mol. mass = 2 \\(\\times{}\\) V.D. = 2 \\(\\times{}\\) 30 = 60\\(n = \\frac{{60}}{{30}} = 2\\)Mol. formula = \\({(C{H_2}O)_2} = {C_2}{H_4}{O_2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The vapour density of the methyl ester of an organic monocarboxylic acid is37. What is the molecular weight of the acid','(a)46','(b)60','(c)70','(d)74','','Purification and organic compounds','2','Answer = (d)Molecular mass = \\(2 \\times V.D. = 2 \\times 37 = 74\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Empirical formula of a hydrocarbon containing 80% carbon and 20% hydrogenis','(a)CH','(b)\\(C{H_2}\\)','(c)\\(C{H_3}\\)','(d)\\(C{H_4}\\)','3','Purification and organic compounds','2','Answer =  (c)Elements        No.of molesSimple ratioC = 80%       80/12 = 6.66             1H = 20%         20/1 = 20               3Hence, Empirical Formula = \\(C{H_3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound with C = 40% and H = 6.7% will have the empiricalformula','(a)\\(C{H_2}\\)','(b)\\(C{H_2}O\\)','(c)\\({C_3}{H_6}{O_3}\\)','(d)\\({C_2}{H_4}{O_2}\\)','2','Purification and organic compounds','2','Answer =  (b)Elements            No. of moles       Simple ratio\\(\\begin{array}{p{49pt}p{44pt}p{22pt}p{22pt}}C = 40\\% & 40/12 & 3.33 & 1 \\\\H = 6.7\\% & 6.7/1 & 6.7 & 2 \\\\O = 53.3\\% & 5.33/16 & 3.33 & 1 \\\\\\end{array}\\)Thus, Empirical formula = \\(C{H_2}O\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following relations gives the value of n =','(a)\\(\\frac{{{\\rm{Moleculer Mass}}}}{{{\\rm{AtomicMass}}}}\\)','(b)\\(\\frac{{{\\rm{Molecular Mass}}}}{{{\\rm{Empirical Mass}}}}\\)','(c)\\(\\frac{{{\\rm{Empirical Mass}}}}{{{\\rm{Molecular Mass}}}}\\)','(d)None of these','2','Purification and organic compounds','2','Answer =  (b)\\(n =\\frac{{{\\rm{Molecular}}\\,\\,{\\rm{mass}}}}{{{\\rm{Emperical}}\\,\\,{\\rm{mass}}}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound containing C, H and N gave following analysis : C =40%, H = 13.33% and N = 46.67%. Its empirical formula would be','(a)\\({C_2}{H_7}{N_2}\\)','(b)\\(C{H_5}N\\)','(c)\\(C{H_4}N\\)','(d)\\({C_2}{H_7}N\\)','3','Purification and organic compounds','2','Answer =  (c) Element  No. ofmoles   Simple ratioC = 40%             40/12            3.33    1H = 13.33%       13.33/1         13.33   4N = 46.67%       46.67/14         3.33  1Thus formula \\(C{H_4}N\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','If a compound on analysis was found to contain C = 18.5%, H = 1.55%, Cl =55.04% and O = 24.81%, then its empirical formula is','(a)\\(CHClO\\)','(b)\\(C{H_2}ClO\\)','(c)\\({C_2}{H_2}OCl\\)','(d)\\(ClC{H_2}O\\)','1','Purification and organic compounds','2','Answer =  (a)Elements        No. of moles       Simple ratioC = 18.5%         18.5/12      \\(\\Rightarrow{}\\)1.54      1H = 1.55%         1.55/1        \\(\\Rightarrow{}\\)1.55      1Cl = 55.04%      55.04/35.5 \\(\\Rightarrow{}\\)1.55       1O = 24.81%      24.81/16     \\(\\Rightarrow{}\\)1.55      1Hence, formula = \\(CHClO\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In kjeldahl\\'s method, \\(CuS{O_4}\\) acts as ','(a)Oxidising agent','(b)Reducing agent','(c)Hydrolysing agent','(d)Catalytic agent','4','Purification and organic compounds','2','Answer =  (d)K\\(= \\frac{{32}}{{Answer = } \\times \\frac{{0.35}}{{0.Answer = }} \\times Answer = = 18.52\\%\\,\\,gm.\\)jeldahl\\'s method depends upon the fact that most of the organic compoundscontaining nitrogen are quantitatively decomposed to give\\({(N{H_4})_2}S{O_4}\\)when heated strongly with conc. \\({H_2}S{O_4}\\). In thismethod \\(CuS{O_4}\\) acts as catalytic agent.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In the qualitative analysis of nitrate a brown ring is formed due to theformation of','(a)\\(N{O_2}\\)','(b)\\(FeS{O_4}N{O_2}\\)','(c)\\({N_2}O.FeS{O_4}\\)','(d)\\(FeS{O_4}.NO\\)','4','Purification and organic compounds','2','Answer =  (d)Nitrates on reaction with conc. \\({H_2}S{O_4}\\) and \\(FeS{O_4}\\) give abrown ring due to formation of \\(FeS{O_4}.NO\\) or  \\([Fe{({H_2}O)_5}NO]S{O_4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Percentage composition of an organic compounds is as follows:C = 10.06, H = 0.84, Cl = 89.10. Which of the following corresponds to itsmolecular formula if the vapour density is 60.0 ','(a)\\(C{H_2}C{l_2}\\)','(b)\\(CHC{l_3}\\)','(c)\\(C{H_3}Cl\\)','(d)None of these','','Purification and organic compounds','2','Answer = (b)Molecular of weight of \\(CHC{l_3}\\)is 120',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The percentage of \\({N_2}\\) in urea is about ','(a)18.05','(b)28.29','(c)46.66','(d)85.56','3','Purification and organic compounds','2','Answer =  (c)Urea \\((N{H_2}CON{H_2})\\)has molecular wt. 60 and wt. of Nitrogen is 28In 60 gm of urea nitrogen present = 28 gmIn Answer = gm of urea nitrogen present \\( = {({C_3}{H_5}{O_2})_2} ={C_6}{H_{10}}{O_4}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following is the best scientific method to test the presence ofwater in a liquid','(a)Use of anhydrous copper sulphate','(b)Use of litmus paper','(c)Taste','(d)Smell','1','Purification and organic compounds','2','Answer =  (a)Anhydrous \\(CuS{O_4}\\) is used to test presence of water in any liquidbecause it changes its colour white to blue.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound has been found to possess the Empirical formula\\(C{H_2}O\\) and molecular weight 90. The molecular formula of it is \\((C =12,\\,\\,H = 1,\\,{\\rm{and}}\\,\\,O = 16)\\)','(a)\\({C_3}{H_6}{O_3}\\)','(b)\\(C{H_2}O\\)','(c)\\({C_2}{H_6}{O_2}\\)','(d)\\({C_2}{H_2}O\\)','1','Purification and organic compounds','2','Answer =  (a)Molecular weight of \\({C_3}{H_6}{O_3}\\) is 90.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound containing carbon hydrogen and oxygen contains 52.20%carbon and 13.04% hydrogen. Vapour density of the compound is 23. Its molecularformula will be ','(a)\\({C_2}{H_6}O\\)','(b)\\({C_3}{H_8}O\\)','(c)\\({C_4}{H_8}O\\)','(d)\\({C_5}{H_{10}}O\\)','1','Purification and organic compounds','2','Answer =  (a)Molecular weight = V.D. \\(\\times{}\\) 2 = 23 \\(\\times{}\\) 2 = 46Molecular weight of \\({C_2}{H_6}O = 46\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The Empirical formula of a compound is \\(C{H_2}O\\) and its molecular weightis  The molecular formula of the compound is','(a)\\({C_2}{H_4}{O_2}\\)','(b)\\({C_3}{H_6}{O_3}\\)','(c)\\({C_4}{H_8}{O_4}\\)','(d)\\(C{H_2}O\\)','3','Purification and organic compounds','2','Answer =  (c)Molecular weight of \\({C_4}{H_8}{O_4}\\) is ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','In Victor Mayer\\'s method 0.2 gm of an organic substance displaced 56 ml ofair at STP the molecular weight of the compound','(a)56','(b)112','(c)80','(d)28','3','Purification and organic compounds','2','Answer =  (c)Molecular mass\\(n = \\frac{{M.wt}}{{E.F.wt}} = \\frac{{Answer = }{{73}} = 2\\)\\(= \\frac{{0.2}}{{56}} \\times Answer = 0 = 80.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following compounds is used as a refrigerants','(a)\\(N{H_3}\\)','(b)\\(C{H_2}{F_2}\\)','(c)\\(CC{l_4}\\)','(d)\\(C{H_3}COON{H_4}\\)','1','Purification and organic compounds','2','Answer =  (a)Liquid ammonia is used as a coolant in ice factories and cold storages.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The latest technique for the purification of organic compounds is ','(a)Fractional distillation','(b)Chromatography','(c)Vacuum distillation','(d)Crystallisation','2','Purification and organic compounds','2','Answer =  (b) Chromatography is the latest technique for the purification of organiccompounds. Chromatography are of various type viz. Column chromatography, gaschromatography, paper chromatography etc.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The presence of halogen, in an organic compounds, is detected by','(a)Crystallisation','(b)Fractional crystallisation','(c)Distillation','(d)Steam distillation','3','Purification and organic compounds','2','Answer =  (c)Halogens are detected by Beilstein\\'s test. In this test, a copper wireis dipped in original solution and heated in a bunsen burner flame. Green colouris imparted to the flame, due to the formation of a volatile copper halide. Thisproves the presence of halogen.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Nitrating mixture is ','(a)Fuming nitric acid','(b)Mixture of conc. \\({H_2}S{O_4}\\) and conc. \\(HN{O_3}\\)','(c)Mixture of nitric acid and anhydrous zinc chloride','(d)None of these','2','Purification and organic compounds','2','Answer =  (b)The mixture of conc.\\({H_2}S{O_4}\\) and conc. \\(HN{O_3}\\) is called nitrating mixture. It is used inthe nitration of aryl compounds.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Quantitative measurement of nitrogen in an organic compounds is done by themethod ','(a)Berthelot method','(b)Belstein method','(c)Lassaigne test','(d)Kjheldahl\\'s method','4','Purification and organic compounds','2','Answer =  (d)Kjeldahl\\'s and Duma\\'s methods are used for the quantitative estimationof nitrogen in an organic compound. In the Kjeldahl method, the nitrogen elementof organic compound is changed to the ammonia.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which kind of fission is favoured by sunlight','(a)Heterolytic fission(b)Homolytic fission(c)Both ','','(b)Homolytic fission(c)Both (a) and ','(b)(d)None of these','2','Purification and organic compounds','2','Answer =  (b)Homolytic fission is favoured by sunlight. In it, each bonded atom takesaway its shared electrons and thus free radicals are produced.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The ammonia evolved from the treatment of 0.30 g of an organic compound forthe estimation of nitrogen was passed in mL of 0.1 M sulphuric acid. Theexcess of acid required 20 mL of 0.5 M sodium hydroxide solution for completeneutralization. The organic compound is','(a)Urea','(b)Benzamide','(c)Acetamide','(d)Thiourea','1','Purification and organic compounds','2','Answer =  (a)Equivalent of \\(N{H_3}\\) evolved\\( = \\frac{{Answer = \\times 0.1 \\times 2}}{{Answer = }} - \\frac{{20 \\times 0.5}}{{Answer = }}\\)\\(= \\frac{1}{{Answer = }\\)percent of nitrogen in the unknown organic compound\\( = \\frac{1}{{Answer = } \\times \\frac{{14}}{{0.3}} \\times Answer = = 46.6\\)%percent of nitrogen in urea \\({(N{H_2})_2}CO\\)\\( = \\frac{{14 \\times 2}}{{60}} \\times Answer = = 46.6\\)%\\(\\therefore \\) The compound must be urea.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The best method for the separation of naphthalene and benzoic acid fromtheir mixture is  ','(a)Chromatography','(b)Crystallisation','(c)Distillation','(d)Sublimation','2','Purification and organic compounds','2','Answer =  (b) Mixture of benzoic acid and naphthalene can be separated from hot waterin which benzoic acid dissolves but naphthalene does not.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','A compound has an empirical formula \\({C_2}{H_4}O\\). An independent analysisgave a value of 16 for its molecular mass. What is the correct molecularformula','(a)\\({C_4}{H_4}{O_5}\\)','(b)\\({C_{10}}{H_{12}}\\)','(c)\\({C_7}{O_3}\\)','(d)\\({C_6}{H_{12}}{O_3}\\)(e)\\({C_4}{H_8}{O_5}\\)','4','Purification and organic compounds','2','Answer =  (d)Empirical formula weight \\({C_2}{H_4}O\\)\\(= (12 \\times 2 + 4 + 16) = 44\\)Molecular formula \\( = \\frac{{{\\rm{mol}}{\\rm{. wt}}}}{{{\\rm{eq}}{\\rm{. formulawt}}{\\rm{.}}}} \\times \\) Emp. Formula\\(= \\frac{{',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','An organic compound has an empirical formula \\(C{H_2}O\\), its vapour densityis 45. The molecular formula of the compounds is  ','(a)\\(C{H_2}O\\)','(b)\\({C_2}{H_5}O\\)','(c)\\({C_2}{H_2}O\\)','(d)\\({C_3}{H_6}{O_3}\\)','4','Purification and organic compounds','2','Answer =  (d)Mol. wt = 2 \\( \\times \\) Vap. Density\\(= 2 \\times 45 = 90\\)Empirical formula weight\\(= 12 + 2 + 16 = 30\\)\\(\\therefore \\,\\,\\,n = \\frac{{{\\rm{mol}}{\\rm{. wt}}{\\rm{.}}}}{{{\\rm{empiricalformula wt}}{\\rm{.}}}}\\)\\(= \\frac{{90}}{{30}} = 3\\)\\(\\therefore \\)  Molecular formula of the compounds\\( = {(C{H_2}O)_3}\\)\\(= {C_3}{H_6}{O_3}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','Which of the following pair of the species has the same percentage of carbon','(a)\\(C{H_3} - C{H_2} - \\mathop {\\mathop {C\\,\\,\\,\\,\\,\\,\\,\\, -\\,\\,\\,\\,\\,\\,}\\limits_{\\,\\,|\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,}^{\\mathop{\\,\\,|\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,\\,}\\limits^{\\,C{H_3}C{H_2}\\,\\,\\,\\,}} }\\limits_{\\,\\,C{H_2}C{H_3}\\,\\,\\,\\,\\,} \\mathop {CH\\, - \\,}\\limits^{\\mathop{|\\,\\,\\,\\,\\,\\,}\\limits^{C{H_3}} \\,\\,\\,\\,\\,\\,} C{H_2} - {(C{H_2})_2} - C{H_3}\\)and \\({C_2}{H_5}OH\\)','(b)\\({C_6}{H_{12}}{O_6}\\) and \\({C_{12}}{H_{22}}{O_{11}}\\)','(c)\\({C_6}{H_{12}}{O_6}\\) and \\({C_{12}}{O_{22}}{O_{11}}\\)','(d)\\({C_{12}}{O_{22}}{O_{11}}\\) and \\({C_6}{H_{12}}{O_6}\\)','4','Purification and organic compounds','2','Answer =  (d)\\(C{H_3}COOH\\) and \\({C_6}{H_{12}}{O_6}\\) both have same percentage ofcarbon i.e. 40%.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','How will you separate a solution (miscible) of benzene \\( + CHC{l_3}\\) ','(a)Sublimation ','(b)Filtration','(c)Distillation ','(d)Crystallisation','','Purification and organic compounds','2','Answer = (c)Distillation particularly fractional distillation because the boilingpoint of benzene \\(({80^o}C)\\) and chloroform \\(({61.5^o}C)\\) are close.Fractional distillation involves repeated distillations and condensations, in afractionating column. As a result of distillation and condensation at each pointof the fractionating column, the vapours rising up become richer in more volatilecomponent and the liquid falling back into the flask becomes richer in lessvolatile component. Thus, the low boiling liquid distils first while the higherboiling liquid distils afterwards.',CURRENT_TIMESTAMP);
