INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Chemistry','The ratio of close-packed atoms to tetrahedral holes in cubic close packingis','(a)1 : 1','(b)1 : 2','(c)1 : 3','(d)2 : 1','2','D and F Block Elements','2','Answer =  (b)Every constituent has two tetrahedral voids. In ccp lattice atoms\\(= 8 \\times \\frac{1}{8} + 6 \\times \\frac{1}{2} = 4\\)\\(\\therefore{}\\) Tetrahedral void \\( = 4 \\times 2 = 8\\),Thus ratio \\( = 4:8::1:2\\).',CURRENT_TIMESTAMP);
